import React, { Component } from 'react';                    
import {Fade} from 'react-reveal';
import '../styles/Landing.css';

import logo from '../images/nextgenlogo.png';
import video from '../images/nextgenvid.mp4';
class Landing extends Component {

    constructor(props) {
        super(props);
    }


    render() {
        return (
            <div>
                <div className="nav-container">

                </div>
                <div id="intro" className="intro-container">
                    <Fade duration={2000}>
                        <img id="intro-logo" src={logo}></img>
                    </Fade>
                    <div className="half-section">
                        <Fade delay={2000}>
                            <p className="intro-text">A school seeking to lead the next generation <br/> of barbers towards success.
                            </p>
                        </Fade>

                        {/* <button className="dark-btn">Preview</button> */}
                    </div>

                    <div className="black-gradient">
                    </div>

                </div>
                <div ref={this.previewRef} className="preview-container">
                    <div className="intro-text-container">
                        {/* <Fade delay={500}>
                            <p className="intro-text">A school seeking to lead the next generation of barbers towards success.
                            </p>
                        </Fade>
                        <Fade delay={1500}>
                            <p className="intro-text">Take a quick look at what Next Gen has to offer:
                            </p>
                        </Fade> */}
                    </div>


                    <div className="video-container">
                        {/* <Fade delay={500}>
                            <h1 id="quote">“Our experience leads to yours.”</h1>
                        </Fade> */}
                        <div class></div>
                        <video className="preview-video noSelect" src={video} type="video/mp4" controls loop>
                        </video>
                    </div>


                </div>
                <div className="instructors-container">

                </div>
            </div>

        );
    }

}

export default Landing;
