import React, {useState} from 'react';
import {NavLink} from "react-router-dom"; 
import {Fade} from 'react-reveal';
import Lottie from 'lottie-web-react';                            

import '../styles/Nav.css';
import logo from '../images/xlogo.png';
import menuicon from '../images/menuicon.png';
import RegisterBtn from '../components/RegisterBtn';

const Nav = (props) => {

    const animOptions = {
        renderer: 'svg',
        loop: false,
        autoplay: true, 
        animationData: require('../helpers/burger.json')
    }
    // const [showLogo, setShowLogo] = useState(false);
    const [showSlider, setShowSlider] = useState(false);

    const toggleSlider = () => {
        setShowSlider(!showSlider);
    }

    return (
        <div className={props.hide ? "nav-container hide-nav" : "nav-container"}>

            <NavLink to="/">
                <img id="responsiveLogo" className="nav-logo" src={logo}></img>
            </NavLink>
            <Lottie
                id="menuIcon"
                options={animOptions}
                playingState='play'>
            </Lottie>
            {/* <img id="menuIcon" src={menuicon} onClick={toggleSlider}></img> */}

            <ul id="staticNav">
                <li>
                    <NavLink className="nav-link" to="/about"> &nbsp; About &nbsp;</NavLink>
                </li>
                <li>
                    <NavLink className="nav-link" to="/">
                        <img className="nav-logo" src={logo}></img>
                    </NavLink>
                </li>
                <li>
                    <NavLink className="nav-link" to="/workshops">Workshops</NavLink>                
                </li>
            </ul>

            {showSlider?    
                <Fade top appear={false}>
                    <div className="slider-container">
                        <ul id="sliderNav">
                            <Fade top>
                                <li>
                                    <NavLink className="nav-link" to="/about">About</NavLink>
                                </li>
                            </Fade>
                            <Fade top delay={100}>
                                <li>
                                    <NavLink className="nav-link" to="/workshops">Workshops</NavLink>
                                </li>
                            </Fade>
                            <Fade top delay={200}>
                                <li>
                                    <RegisterBtn/>
                                </li>
                            </Fade>
                        </ul>
                    </div> 
                </Fade>
                : null
            }


        </div>    
    )
}

export default Nav;
