import React, { Component } from 'react';   
import { Route, NavLink, Switch } from "react-router-dom";          
import {Fade, Flip} from 'react-reveal';
import Modal from 'react-modal-slider';

import 'react-modal-slider/lib/main.css';
import '../styles/Home.css';

import Nav from '../components/Nav';
import Landing from '../components/Landing';
import About from '../components/About';
import Foundations from '../components/Foundations';
import Footer from '../components/Footer';

import HomeCover from '../components/HomeCover';
import AboutCover from '../components/AboutCover';
import FoundationsCover from '../components/FoundationsCover';


import logo from '../images/logodark.png';
import menuicon from '../images/menuicon.png';


const menuClose = {
    top: '-35vh'
}

const menuOpen = {
    top: 0
}

var cover, halfpoint, prevSection, instructSection, workshopSection, connectSection;


class Home extends Component {
    constructor(props) {
        super(props);
        this.state = {
            showLand: true,
            showPrev: false,
            isOpen: false
        }
    }

    componentDidMount() {
        this.prev = window.scrollY;
        window.addEventListener('scroll', this.handleScroll);
        cover = document.getElementById("coverRef");
        halfpoint = cover.offsetHeight / 2; 
        // prevSection = document.getElementById("prevSection");
    }

    componentWillUnmount() {
        window.removeEventListener('scroll', this.handleScroll);
    }

    handleScroll = (event) => {

        // var prevDiff = prevSection.offsetTop - window.pageYOffset;

        // if(prevDiff > halfpoint) {
        //     this.setState({showLand: true,
        //                     showPrev: false,
        //                     showInstruct: false, 
        //                     showWork: false, 
        //                     showConnect: false});
        // }

        // if(prevDiff < halfpoint){
        //     this.setState({showLand: false,
        //                     showPrev: true,
        //                     showInstruct: false, 
        //                     showWork: false, 
        //                     showConnect: false});
        // } 
    }

    toggleVisibleModal = () => {
        this.setState({isOpen: !this.state.isOpen});
    }



    render() {
        return (
            <div className="home-wrapper">

                <Fade left>
                    <div id="coverRef" className="fixed-cover-container">
                        {/* <img id="menuicon" src={menuicon} onClick={this.toggleVisibleModal}></img> */}
                        <div className="menu-container">
                            <ul>
                                <li>
                                    <NavLink to="/about">About</NavLink>
                                </li>
                                <li>
                                    <NavLink to="/foundations">Workshops</NavLink>
                               </li>
                                <li>
                                    <button className="btn">Register</button>
                                </li>
                            </ul>
                        </div>

                        <Switch>
                            <Route path="/" exact component={HomeCover}></Route>
                            <Route path="/about" exact component={AboutCover}></Route>
                            <Route path="/foundations" exact component={FoundationsCover}></Route>
                        </Switch>              

                    </div>
                </Fade>

                <Switch>
                    <Route path="/" exact component={Landing}/>
                    <Route path="/about" exact component={About}/>
                    <Route path="/foundations" exact component={Foundations}/>
                </Switch>

                <Footer/>

            </div>
        );
    }
}

export default Home;
