import React, { Component } from 'react';   
import {Fade, Flip} from 'react-reveal';
import Modal from 'react-modal-slider';

import 'react-modal-slider/lib/main.css';
import '../styles/Home.css';

import Nav from '../components/Nav';

import logo from '../images/logodark.png';
import manual from '../images/manual.png';
import silver from '../images/silver.png';
import gold from '../images/gold.png';
import video from '../images/nextgenvid.mp4';
import foundations from '../images/foundations.png';
import techniques from '../images/techniques.png';
import connect from '../images/connect2.jpg';
import menuicon from '../images/menuicon.png';

const videoStyle = {
    filter: 'grayscale(100%)'
}

const menuClose = {
    top: '-35vh'
}

const menuOpen = {
    top: 0
}

var cover, halfpoint, prevSection, instructSection, workshopSection, connectSection;


class Home extends Component {
    constructor(props) {
        super(props);
        this.state = {
            play: false,
            showLand: true,
            showPrev: false,
            showInstruct: false, 
            showWork: false, 
            showConnect: false,
            isOpen: false
        }
    }

    componentDidMount() {
        this.prev = window.scrollY;
        window.addEventListener('scroll', this.handleScroll);
        cover = document.getElementById("coverRef");
        halfpoint = cover.offsetHeight / 2; 
        prevSection = document.getElementById("prevSection");
        instructSection = document.getElementById("instructSection");
        workshopSection = document.getElementById("workshopSection");
        connectSection = document.getElementById("connectSection");
    }

    componentWillUnmount() {
        window.removeEventListener('scroll', this.handleScroll);
    }

    handleScroll = (event) => {

        var prevDiff = prevSection.offsetTop - window.pageYOffset;
        var instructDiff = instructSection.offsetTop - window.pageYOffset;
        var workshopDiff = workshopSection.offsetTop - window.pageYOffset;
        var connectDiff = connectSection.offsetTop - window.pageYOffset;

        if(prevDiff > halfpoint) {
            this.setState({showLand: true,
                            showPrev: false,
                            showInstruct: false, 
                            showWork: false, 
                            showConnect: false});
        }

        if(prevDiff < halfpoint){
            this.setState({showLand: false,
                            showPrev: true,
                            showInstruct: false, 
                            showWork: false, 
                            showConnect: false});
        } 

        if (instructDiff < halfpoint) {
            this.setState({showLand: false,
                            showPrev: false,
                            showInstruct: true, 
                            showWork: false, 
                            showConnect: false});        
        }

        if (workshopDiff < halfpoint) {
            this.setState({showLand: false,
                            showPrev: false,
                            showInstruct: false, 
                            showWork: true, 
                            showConnect: false});        
        }

        if (connectDiff < halfpoint) {
            this.setState({showLand: false,
                            showPrev: false,
                            showInstruct: false, 
                            showWork: false, 
                            showConnect: true});        
        }

    }

    playPreview = () => {
        console.log("play");
        this.setState({play: true});
        var vid = document.getElementById("video");
        vid.play();
    }

    paused = () => {
        console.log("paused!");
        this.setState({play: false});
    }

    toggleVisibleModal = () => {
        this.setState({isOpen: !this.state.isOpen});
    }



    render() {
        return (
            <div className="home-wrapper">

                <Fade left>
                    <div id="coverRef" className="fixed-cover-container">
                        {/* <img id="menuicon" src={menuicon} onClick={this.toggleVisibleModal}></img> */}
                        <div className="menu-container">
                            <ul>
                                <li>About</li>
                                <li>Workshops</li>
                                <li>
                                    <button className="btn">Register</button>
                                </li>
                            </ul>
                        </div>

                        <img id="logoDark" src={logo}></img>
                        {this.state.showLand? 
                            <Fade delay={800}>
                               <p className="intro-desc">A school seeking to lead the next generation of barbers towards success.
                                </p> 
                            </Fade>
                             : null}

                        {this.state.showPrev? 
                            <p className="intro-desc">Take a look at what next gen has to offer:
                            </p> : null}

                        {this.state.showInstruct? 
                            <p className="intro-desc">“Our experience leads to yours”
                            </p> : null}

                        {this.state.showWork? 
                            <p className="intro-desc">Our Workshops:
                            </p> : null}

                        {this.state.showConnect? 
                            <p className="intro-desc">Connect with us
                            </p> : null}

                    </div>
                </Fade>

                <div className="section landing-section">
                    <Fade delay={800}>
                        <div className="manual-container">
                            <img id="manual" src={manual}></img>
                            <img id="gold" src={gold}></img>
                        </div>
                    </Fade>

                </div>

                <div id="prevSection" className="section preview-section">
                    {this.state.play? null: 
                        <Fade delay={500}>                    
                            <div className="preview-btn" onClick={this.playPreview}/>
                        </Fade>
                    }
                    <div>
                        <video 
                            id="video" 
                            src={video} 
                            type="video/mp4" 
                            controls
                            style={this.state.play? null: videoStyle}
                            controls={this.state.play? true : false} 
                            onPause={this.paused}
                            /> 
                    </div>

                </div>

                <div id="instructSection" className="section instructor-section">
                    <Fade>
                        <div className="instructors-window">
                            <div id="barber1" className="barber-container"></div>
                            <div id="barber2" className="barber-container"></div>

                            <div className="black-gradient2"></div>

                            <Fade delay={1200}>
                                <div className="instructors-txt-container">
                                    <h1 id="instructors">Our instructors</h1>
                                    <p id="instructors-desc">are passionate barbers dedicated to sharing their knowledge acquired over 10+ years of experience</p>
                                </div>
                            </Fade>

                            <div id="name-left" className="name-container">
                                <h4>Navarone Paul</h4>
                                <p>@WOWNVRN</p>
                            </div>

                            <div id="name-right" className="name-container">
                                <h4>Christopher Gonzales</h4>
                                <p>@CHRISGTHEBARBER</p>
                            </div>
                        </div>
                    </Fade>
                </div>

                <div id="workshopSection" className="section workshop-section">
                        <Fade left delay={500}>
                            <div className="booklet-wrapper">
                                <div className="booklet-case">
                                    <img src={foundations} className="book-img"></img>
                                    <img src={gold} className="scissors"></img>
                                </div>                              
                                <p className="booklet-desc">
                                    This course seeks to teach students the basics of fading, scissor work, and beard work.
                                </p>
                            </div>
                        </Fade>

                        <Fade right delay={500}>
                            <div className="booklet-wrapper">
                                <div className="booklet-case">
                                    <img src={techniques} className="book-img"></img>
                                    <img src={silver} className="scissors"></img>
                                </div>
                                <p className="booklet-desc">
                                    This course seeks to teach students the basics of fading, scissor work, and beard work. 
                                </p>
                            </div>
                        </Fade>
                </div>

                <div id="connectSection" className="section connect-section">
                    <img id="connectImg" src={connect}></img>
                </div>

                <div className="section footer">

                </div>

            </div>
        );
    }
}

export default Home;
